import React from 'react'
import { Button, Card, Col, Container, Row } from 'react-bootstrap'
import { deleteBlog, deleteBlogbyId } from '../Features/Blogs/blogsSlice'
import { useDispatch } from 'react-redux'
const Blog = ({ blog }) => {
  const dispatch = useDispatch()

  return(
    <Card border="primary" style={{ width: '80rem' ,margin:'15px'  }}>
      <Card.Header>Author :{blog.author}</Card.Header>
      <Card.Body>
        <Card.Title>Title: {blog.title}</Card.Title>
        <Card.Text>
          {blog.name}
        </Card.Text>
        <Button onClick={()=>dispatch(deleteBlogbyId(blog.id))} variant="primary">Delete</Button>
      </Card.Body>
    </Card>


  )
}

export default Blog
