import React, { useEffect, useState } from 'react'
import Togglable from './components/Togglable'
import Blog from './components/Blog'
import blogService from './services/blogs'
import BlogForm from './components/BlogForm'
import loginService from './services/login.js'
import LoginForm from './components/loginform'
import { Button } from 'react-bootstrap'
import {useSelector, useDispatch} from 'react-redux'
import { createBlog, deleteBlog, initializeBlogs } from './Features/Blogs/blogsSlice'

const App = () => {
const dispatch = useDispatch()
  const blogs = useSelector(state => state.blogs)
  const addBlog = blog => {(
    dispatch(createBlog(blog))
   )
  }
  //---------------------------hooks---------------- //
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  //fetching blogs

  useEffect(() => {
    dispatch(initializeBlogs())

  }, [])
  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])
  // ----------------------------------------------------------------//
  const handleLogin = async e => {
    e.preventDefault()
    try {
      const user = await loginService.login({
        username,
        password,
      })
      blogService.setToken(user.token)
      window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user))
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (err) {
      console.log(err)
    }
  }
  // LOGINFORM
  const loginForm = () => (
    <Togglable buttonLabel="log in">
      <LoginForm
        username={username}
        password={password}
        handleUsernameChange={({ target }) => setUsername(target.value)}
        handlePasswordChange={({ target }) => setPassword(target.value)}
        handleSubmit={handleLogin}
      />
    </Togglable>
  )
  // BLOGFORM
  const blogForm = () => (
    <Togglable buttonLabel="new blog">
      <BlogForm createBlog={addBlog} />
      <Button
        variant="danger"
        onClick={() => {
          setUser(null)
          localStorage.clear()
        }}
      >
        Logout
      </Button>
    </Togglable>
  )
  const userBlogs = user&&blogs.filter(blog => blog.user.username === user._id)
  console.log(user,'user')
  console.log(blogs)
  console.log(userBlogs,'userBlogs')
  return (
    <div style={{ class: 'w3-highway-schoolbus' }}>
      {user === null ? (
        loginForm()
      ) : (
        <div>
          <div style={{ display: 'flex' }}>
            <p>{user.name} logged in</p>
          </div>
          {blogForm()}  {
          userBlogs.map(blog => <Blog key={blog.id} blog={blog} />)}
        </div>
      )}

    </div>
  )
}

export default App
