import React, { useEffect, useRef } from 'react'
import LoginForm from './components/LoginForm'
import Togglable from './components/Togglable'
import BlogForm from './components/BlogForm'
import { useDispatch, useSelector } from 'react-redux'
import { initializeBlogs } from './features/blogs/blogsSlice'
import { initializeUser } from './features/users/authSlice'
import { initializeUsers } from './features/users/usersSlice'
// disable eslint unused variables for all file

const user = 'gordun209'
const App = () => {


  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(initializeUser())
    dispatch(initializeBlogs())
    dispatch(initializeUsers())

  }, [dispatch])
  const users = useSelector(state => state.users)
  const blogs = useSelector(state => state.blogs)
  const user = useSelector(state => state.user)
  console.log(users, blogs, user)
  const noteFormRef = useRef(null)
  const loginForm = () => (
    <Togglable buttonLabel='login'>
      <LoginForm />
    </Togglable>
  )

  const blogForm = () => (
    <Togglable buttonLabel='new blog' ref={noteFormRef}>
      <BlogForm />
    </Togglable>
  )

  return (
    <div>
      <h1>blog App</h1>
      {user ?
        loginForm() :
        <div>
          <h2>blogs</h2>
          <p>{'gordun209'} logged in</p>
          {blogForm()}
        </div>
      }

    </div>
  )
}

export default App
