import React, { useEffect } from 'react'
import LoginForm from './features/users/LoginForm'
import Togglable from './components/Togglable'
import BlogForm from './features/blogs/BlogForm'
import { useDispatch, useSelector } from 'react-redux'
import { initializeBlogs } from './features/blogs/blogsSlice'
import { initializeUser, logoutUser } from './features/users/authSlice'
import { initializeUsers } from './features/users/usersSlice'
import Blog from './components/Blog'

const App = () => {


  const dispatch = useDispatch()
  // initialize all state
  useEffect(() => {
    dispatch(initializeUser())
    dispatch(initializeBlogs())
    dispatch(initializeUsers())

  }, [dispatch])
  // select users blogs and logged in user
  // const users = useSelector(state => state.users)
  const blogs = useSelector(state => state.blogs)
  const user = useSelector(state => state.user.user)
  const loginForm = () => (
    <Togglable buttonLabel='login'>
      <LoginForm />
    </Togglable>
  )
  const blogForm = () => (
    <Togglable buttonLabel='new blog'>
      <BlogForm />
      <button onClick={() => dispatch(logoutUser())}>logout</button>
    </Togglable>
  )
  const userBlogs = () => {
    return user && blogs.filter(blog => blog.user.username !== user.username)
  }
  return (
    <div>
      <h1>blog App</h1>
      {!user ?
        loginForm()
        :
        <div>
          <h2>blogs</h2>
          {blogForm()}
          {user && userBlogs().map(blog => <Blog key={blog.id} blog={blog} />)}
        </div>
      }
    </div>
  )
}

export default App
