import { createSlice } from '@reduxjs/toolkit'
import blogsService from '../../services/blogs'

const blogsSlice = createSlice({
  name: 'blogs',
  initialState: {
    blogs: [],
    error: null,
    loading: false,
  },
  addBlog: (state, action) => {
    state.push(action.payload)
  },
  setBlogs: (state, action) => {
    state.push(action.payload)
  },
})
export const getAllBlogs = () => async dispatch => {
  dispatch(blogsSlice.actions.setLoading(true))
  try {
    const blogs = await blogsService.getAll()
    dispatch(blogsSlice.actions.setBlogs(blogs))
  } catch (error) {
    dispatch(blogsSlice.actions.setError(error))
  } finally {
    dispatch(blogsSlice.actions.setLoading(false))
  }
}


export const createBlog = (title, author, url) => async dispatch => {
  const blog = await blogsService.create({ title, author, url })
  dispatch(addBlog(blog))
}

export const { addBlog, setBlogs } = blogsSlice.actions
export default blogsSlice.reducer
