import React, { useEffect, useState } from 'react'
import Togglable from './components/Togglable'
import Blog from './components/Blog'
import { useFetchBlogsQuery } from '../src/Features/Blogs/BlogApi'
import blogService from './services/blogs'
import BlogForm from './components/BlogForm'
import loginService from './services/login.js'
import LoginForm from './components/loginform'
import { Button, Col, Container, Row } from 'react-bootstrap'
const App = () => {
  //ADDING BLOG FUNCTION

  const addBlog = blog => {
    blogService.create(blog).then(returnedBlog => {
      setBlogs(blogs.concat(returnedBlog))
    })
  }
  //hooks
  const { data, isSuccess } = useFetchBlogsQuery()
  const [blogs, setBlogs] = useState(data)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  //fetching blogs
  useEffect(() => {
    setBlogs(data)
  }, [data])
  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])
  const handleLogin = async e => {
    e.preventDefault()
    try {
      const user = await loginService.login({
        username,
        password,
      })
      blogService.setToken(user.token)
      window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user))
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (err) {
      console.log(err)
    }
  }
  // LOGINFORM
  const loginForm = () => (
    <Togglable buttonLabel="log in">
      <LoginForm
        username={username}
        password={password}
        handleUsernameChange={({ target }) => setUsername(target.value)}
        handlePasswordChange={({ target }) => setPassword(target.value)}
        handleSubmit={handleLogin}
      />
    </Togglable>
  )
  // BLOGFORM
  const blogForm = () => (
    <Togglable buttonLabel="new blog">
      <BlogForm createBlog={addBlog} />
      <Button
        variant="danger"
        onClick={() => {
          setUser(null)
          localStorage.clear()
        }}
      >
        Logout
      </Button>
    </Togglable>
  )
  return (
    <div style={{ class: 'w3-highway-schoolbus' }}>
      {user === null ? (
        loginForm()
      ) : (
        <div>
          <div style={{ display: 'flex' }}>
            <p>{user.name} logged in</p>
          </div>
          {blogForm()}
        </div>
      )}
      {isSuccess && blogs.map(blog => <Blog key={blog.id} blog={blog} />)}
    </div>
  )
}

export default App
