import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap'

const BlogForm = ({ createBlog }) => {
  const [author, setAuthor] = useState('')
  const [title, setTitle] = useState('')
  const [url, setUrl] = useState('')

  const handleAuthorChange = event => {
    setAuthor(event.target.value)
  }
  const handleTitleChange = event => {
    setTitle(event.target.value)
  }
  const handleUrlChange = event => {
    setUrl(event.target.value)
  }
  const addNote = event => {
    event.preventDefault()
    createBlog({
      author: author,
      title: title,
      url: url,
    })

    setUrl('')
    setTitle('')
    setAuthor('')
  }

  return (
    <div style={{background:'cyan', padding:'25px'}}>
      <h2>Create a new blog</h2>
      <Form onSubmit={addNote}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Author</Form.Label>
          <Form.Control style={{backgroundColor:''}} value={author} onChange={handleAuthorChange} type="email" placeholder="Author..." />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Title</Form.Label>
          <Form.Control type="text" placeholder="Title..." value={title} onChange={handleTitleChange}/>
        </Form.Group>   <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Url</Form.Label>
        <Form.Control type="text" placeholder="Url..." value={url} onChange={handleUrlChange}/>
      </Form.Group> <Button variant="primary" type="submit">
        save
      </Button>

      </Form>

    </div>
  )
}

export default BlogForm
