import { configureStore } from '@reduxjs/toolkit'
import authReducer from '../features/users/authSlice'
import blogReducer from '../features/blogs/blogsSlice'

const store = configureStore({
  reducer: {
    auth: authReducer,
    blogs: blogReducer,
  },
})

export default store
